                        "3 * 4" to 12.0,
                        "15 / 3" to 5.0,
                        "2^8" to 256.0,
                        "17 % 5" to 2.0,
                        "2 + 3 * 4" to 14.0,
                        "(2 + 3) * 4" to 20.0,
                        "sqrt(16)" to 4.0,
                        "abs(-42)" to 42.0,
                        "sin(PI/2)" to 1.0,
                        "cos(0)" to 1.0,
                        "ln(E)" to 1.0,
                        "log10(100)" to 2.0,
                        "sqrt(16) + sin(PI/2)" to 5.0,
                        "2 * PI * 5" to (2 * kotlin.math.PI * 5),
                        "-5 + 10" to 5.0,
                        "3^(1/2)" to kotlin.math.sqrt(3.0)
                )

        var passedTests = 0
        var totalTests = testExpressions.size

        for ((expression, expected) in testExpressions) {
            try {
                val expr = parser.parse(expression)
                val result = calc.eval(expr)
                val passed = kotlin.math.abs(result - expected) < 0.0001

                val status = if (passed) "âœ… PASS" else "âŒ FAIL"
                val formattedResult =
                        if (result == result.toLong().toDouble()) {
                            result.toLong().toString()
                        } else {
                            "%.6g".format(result)
                        }

                println("$status | $expression = $formattedResult")

                if (passed) {
                    passedTests++
                } else {
                    println("      Expected: $expected, Got: $result")
                }
            } catch (e: Exception) {
                println("âŒ FAIL | $expression -> Error: ${e.message}")
            }
        }

        println("\nResults: $passedTests/$totalTests tests passed")

        if (passedTests == totalTests) {
            println("ðŸŽ‰ All tests passed! The calculator is working perfectly.")
        }

        // Assert that all tests passed
        assertEquals(totalTests, passedTests, "Not all calculator tests passed")

        println("\nðŸ“– Calculator Features:")
        println("======================")
        println("âœ“ Basic arithmetic: +, -, *, /, ^, %")
        println("âœ“ Parentheses for grouping")
        println("âœ“ Unary operators: -, +")
        println("âœ“ Mathematical functions: sin, cos, tan, sqrt, ln, log10, abs")
        println("âœ“ Constants: PI, E")
        println("âœ“ Proper order of operations")
        println("âœ“ Error handling for invalid input")
        println("âœ“ Interactive CLI interface")
        println("\nðŸš€ To use the interactive calculator, run: ./gradlew run")
    }
}
